import * as ActionTypes from '../actions/actionTypes'
const initialState = {
    counter: 0
};

const stockReducer = (state = initialState,action) => {
    switch(action.type){
        case ActionTypes.INCREMENT:
            //return state 
            return state+1;
        default:
            return state ;
    }
}

export default stockReducer;
